import React, { useMemo } from 'react';
import './OrderConfirmation.css';

const OrderConfirmation = ({ orderNumber, cart, total, customerInfo, onNewOrder, onPhoneOrder }) => {
  const { ricePlatters, juiceExtras } = useMemo(() => {
    const ricePlatters = cart.filter(item => item.category !== 'Fresh Juice Extras');
    const juiceExtras = cart.filter(item => item.category === 'Fresh Juice Extras');
    return { ricePlatters, juiceExtras };
  }, [cart]);

  const getItemIcon = (item) => {
    if (!item.image) return 'üçΩÔ∏è';
    return typeof item.image === 'string' && item.image.startsWith('/') ? 'üçπ' : item.image;
  };

  const handlePrint = () => {
    const printWindow = window.open('', '_blank');
    const printDate = new Date().toLocaleString('vi-VN');

    printWindow.document.write(`
      <!DOCTYPE html>
      <html>
        <head>
          <title>Booking Summary - ${orderNumber}</title>
          <style>
            @media print {
              @page {
                size: 80mm auto;
                margin: 0;
              }
            }
            body {
              font-family: 'Courier New', monospace;
              width: 70mm;
              padding: 10mm;
              margin: 0;
              font-size: 14px;
            }
            .header {
              text-align: center;
              border-bottom: 2px dashed #000;
              padding-bottom: 10px;
              margin-bottom: 10px;
            }
            .header h1 {
              font-size: 18px;
              margin: 5px 0;
            }
            .order-info {
              margin: 10px 0;
            }
            .order-info strong {
              font-size: 12px;
            }
            .items {
              margin: 10px 0;
              border-top: 2px dashed #000;
              padding-top: 10px;
            }
            .item-row {
              display: flex;
              justify-content: space-between;
              margin: 5px 0;
              font-size: 13px;
            }
            .item-name {
              flex: 1;
            }
            .item-qty {
              margin: 0 5px;
            }
            .total {
              border-top: 2px dashed #000;
              padding-top: 10px;
              margin-top: 10px;
              text-align: center;
              font-weight: bold;
              font-size: 16px;
            }
            .footer {
              text-align: center;
              margin-top: 20px;
              border-top: 2px dashed #000;
              padding-top: 10px;
              font-size: 12px;
            }
            .divider {
              border-top: 1px dashed #000;
              margin: 10px 0;
            }
            .section-title {
              font-weight: bold;
              margin: 8px 0 4px;
            }
          </style>
        </head>
        <body>
          <div class="header">
            <h1>üçö SAVORY RICE & SIP üçπ</h1>
            <div class="divider"></div>
            <div style="font-size: 12px;">Booking #${orderNumber}</div>
            <div style="font-size: 11px;">${printDate}</div>
          </div>

          ${customerInfo ? `
            <div class="order-info">
              <strong>Kh√°ch h√†ng:</strong><br/>
              ${customerInfo.customerName}<br/>
              üìû ${customerInfo.customerPhone}<br/>
              üìç ${customerInfo.customerAddress}<br/>
              üìÖ ${customerInfo.eventDate || 'Ch∆∞a c·∫≠p nh·∫≠t'}<br/>
              üïí ${customerInfo.deliveryWindow || 'Theo trao ƒë·ªïi'}<br/>
              üë• ${customerInfo.peopleCount || 'Ch∆∞a c·∫≠p nh·∫≠t'} kh√°ch
              <div class="divider"></div>
            </div>
          ` : ''}

          <div class="items">
            <div class="section-title">M√≥n c∆°m ch√≠nh</div>
            ${ricePlatters.map(item => `
              <div class="item-row">
                <span class="item-name">${item.name}</span>
                <span class="item-qty">${item.quantity}x</span>
                <span>${item.price * item.quantity}k</span>
              </div>
            `).join('')}
            <div class="section-title">N∆∞·ªõc √©p th√™m</div>
            ${juiceExtras.length > 0 ? juiceExtras.map(item => `
              <div class="item-row">
                <span class="item-name">${item.name}</span>
                <span class="item-qty">${item.quantity}x</span>
                <span>${item.price * item.quantity}k</span>
              </div>
            `).join('') : '<div class="item-row"><span class="item-name">Ch∆∞a ƒë·∫∑t n∆∞·ªõc √©p</span><span></span><span>0k</span></div>'}
          </div>

          <div class="total">
            TOTAL: ${total}k VND
          </div>

          <div class="footer">
            ‚úÖ Booking received<br/>
            ƒê·ªôi ng≈© Savory Rice & Sip s·∫Ω li√™n h·ªá x√°c nh·∫≠n.<br/>
            Xin c·∫£m ∆°n!
          </div>
        </body>
      </html>
    `);

    printWindow.document.close();

    setTimeout(() => {
      printWindow.focus();
      printWindow.print();
      printWindow.close();
    }, 250);
  };

  return (
    <div className="order-confirmation">
      <div className="confirmation-content">
        <div className="success-icon">üçΩÔ∏è</div>
        <h1>Booking Confirmed!</h1>
        <div className="order-number">
          <p>Booking Reference</p>
          <h2>{orderNumber}</h2>
        </div>

        {customerInfo && (
          <div className="customer-details">
            <h3>Booking Contact</h3>
            <div className="customer-info-item">
              <strong>Name:</strong> {customerInfo.customerName}
            </div>
            <div className="customer-info-item">
              <strong>Phone:</strong> {customerInfo.customerPhone}
            </div>
            <div className="customer-info-item">
              <strong>Delivery Address:</strong> {customerInfo.customerAddress}
            </div>
            <div className="customer-info-item">
              <strong>Event Date:</strong> {customerInfo.eventDate || 'Pending'}
            </div>
            {customerInfo.deliveryWindow && (
              <div className="customer-info-item">
                <strong>Delivery Window:</strong> {customerInfo.deliveryWindow}
              </div>
            )}
            {customerInfo.peopleCount && (
              <div className="customer-info-item">
                <strong>Guests:</strong> {customerInfo.peopleCount}
              </div>
            )}
            {customerInfo.notes && (
              <div className="customer-info-item">
                <strong>Notes:</strong> {customerInfo.notes}
              </div>
            )}
          </div>
        )}

        <div className="order-details">
          <h3>Rice Platters</h3>
          {ricePlatters.map(item => (
            <div key={item.id} className="order-item">
              <span className="item-emoji">{getItemIcon(item)}</span>
              <div className="item-details">
                <span className="item-name">{item.name}</span>
                <span className="item-quantity">Qty: {item.quantity}</span>
              </div>
              <span className="item-price">{(item.price * item.quantity)}k</span>
            </div>
          ))}

          <h3>Juice Extras</h3>
          {juiceExtras.length > 0 ? juiceExtras.map(item => (
