import React, { useState } from 'react';
import './PaymentVerification.css';

const PaymentVerification = ({ orderNumber, cart, total, onPaymentVerified, onCancel }) => {
  const [paymentProof, setPaymentProof] = useState(null);
  const [customerName, setCustomerName] = useState('');
  const [customerPhone, setCustomerPhone] = useState('');
  const [customerAddress, setCustomerAddress] = useState('');
  const [isUploading, setIsUploading] = useState(false);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setIsUploading(true);
      const reader = new FileReader();
      reader.onloadend = () => {
        setPaymentProof(reader.result);
        setIsUploading(false);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = () => {
    if (!customerName || !customerPhone || !paymentProof) {
      alert('Please fill in all required fields and upload payment proof');
      return;
    }
    onPaymentVerified({
      orderNumber,
      customerName,
      customerPhone,
      customerAddress,
      paymentProof,
      cart,
      total
    });
  };

  return (
    <div className="payment-verification">
      <div className="payment-content">
        <div className="payment-header">
          <div className="success-icon">💳</div>
          <h1>Payment Required</h1>
          <div className="order-number">
            <p>Order Number</p>
            <h2>{orderNumber}</h2>
          </div>
        </div>

        <div className="payment-instructions">
          <h3>📱 Payment Instructions:</h3>
          <div className="qr-section">
            <p className="instruction-text">
              Please transfer <strong>{total}k</strong> to the following account:
            </p>
            <div className="qr-placeholder">
              <div className="qr-code-container">
                <p className="qr-label">Your QR Code</p>
                <div className="qr-code-box">
                  <p className="qr-placeholder-text">📱 Add your QR code image here</p>
                  <p className="qr-note">Place: public/images/qr-code.png or jpg</p>
                </div>
                <div className="bank-info">
                  <p><strong>Bank:</strong> [Your Bank Name]</p>
                  <p><strong>Account:</strong> 0896894688</p>
                  <p><strong>Name:</strong> [Account Holder Name]</p>
                </div>
              </div>
            </div>
          </div>
        </div>

        <div className="customer-form">
          <h3>Your Information:</h3>
          <div className="form-group">
            <label>Full Name *</label>
            <input 
              type="text" 
              value={customerName}
              onChange={(e) => setCustomerName(e.target.value)}
              placeholder="Enter your name"
              required
            />
          </div>
          <div className="form-group">
            <label>Phone Number *</label>
            <input 
              type="tel" 
              value={customerPhone}
              onChange={(e) => setCustomerPhone(e.target.value)}
              placeholder="0896894688"
              required
            />
          </div>
          <div className="form-group">
            <label>Delivery Address</label>
            <input 
              type="text" 
              value={customerAddress}
              onChange={(e) => setCustomerAddress(e.target.value)}
              placeholder="Your delivery address (optional)"
            />
          </div>
        </div>

        <div className="payment-proof-section">
          <h3>Upload Payment Proof *</h3>
          <input
            type="file"
            accept="image/*"
            onChange={handleImageUpload}
            className="payment-proof-input"
            id="payment-proof"
          />
          {paymentProof && (
            <div className="payment-proof-preview">
              <img src={paymentProof} alt="Payment proof" />
              <button onClick={() => setPaymentProof(null)}>Remove</button>
            </div>
          )}
          {!paymentProof && !isUploading && (
            <label htmlFor="payment-proof" className="upload-proof-button">
              📸 Upload Transfer Screenshot
            </label>
          )}
        </div>

        <div className="action-buttons">
          <button className="btn-submit" onClick={handleSubmit}>
            ✅ Confirm & Send Order
          </button>
          <button className="btn-cancel" onClick={onCancel}>
            Cancel Order
          </button>
        </div>

        <p className="info-note">
          ⚠️ Your order will be processed after payment verification
        </p>
      </div>
    </div>
  );
};

export default PaymentVerification;
