export const getMenuSections = (items) => {
  const sections = {};

  items.forEach(item => {
    if (!sections[item.category]) {
      sections[item.category] = [];
    }
    sections[item.category].push(item);
  });

  return sections;
};

export const getSectionTitle = (category) => {
  const titles = {
    "Signature Rice Platters": "üç± Signature Rice Platters",
    "Family Rice Combos": "üë®‚Äçüë©‚Äçüëß‚Äçüë¶ Family Rice Combos",
    "Sides & Salads": "ü•ó Sides & Salads",
    "Fresh Juice Extras": "üçπ Fresh Juice Extras"
  };

  return titles[category] || category;
};
