import React, { useMemo, useState } from 'react';
import './PaymentVerification.css';

const PaymentVerification = ({ orderNumber, cart, total, onPaymentVerified, onCancel }) => {
  const [customerName, setCustomerName] = useState('');
  const [customerPhone, setCustomerPhone] = useState('');
  const [customerAddress, setCustomerAddress] = useState('');
  const [eventDate, setEventDate] = useState('');
  const [deliveryWindow, setDeliveryWindow] = useState('');
  const [peopleCount, setPeopleCount] = useState('');
  const [notes, setNotes] = useState('');

  const { ricePlatters, juiceExtras } = useMemo(() => {
    const ricePlatters = cart.filter(item => item.category !== 'Fresh Juice Extras');
    const juiceExtras = cart.filter(item => item.category === 'Fresh Juice Extras');
    return { ricePlatters, juiceExtras };
  }, [cart]);

  const handleSubmit = () => {
    if (!customerName || !customerPhone || !customerAddress || !eventDate) {
      alert('Please share your name, phone, delivery address and event date before confirming.');
      return;
    }

    onPaymentVerified({
      orderNumber,
      customerName,
      customerPhone,
      customerAddress,
      eventDate,
      deliveryWindow,
      peopleCount,
      notes,
      cart,
      total
    });
  };

  return (
    <div className="payment-verification">
      <div className="payment-content">
        <div className="payment-header">
          <div className="success-icon">üçö</div>
          <h1>Confirm Your Rice Booking</h1>
          <div className="order-number">
            <p>Booking Reference</p>
            <h2>{orderNumber}</h2>
          </div>
        </div>

        <div className="booking-summary">
          <h3>Selected Platters</h3>
          {ricePlatters.length > 0 ? (
            <ul className="summary-list">
              {ricePlatters.map(item => (
                <li key={item.id}>
                  <span>{item.quantity}√ó {item.name}</span>
                  <span>{item.price * item.quantity}k</span>
                </li>
              ))}
            </ul>
          ) : (
            <p className="summary-empty">No rice platters selected yet.</p>
          )}

          <h3>Juice Extras</h3>
          {juiceExtras.length > 0 ? (
            <ul className="summary-list juice">
              {juiceExtras.map(item => (
                <li key={item.id}>
                  <span>{item.quantity}√ó {item.name}</span>
                  <span>{item.price * item.quantity}k</span>
                </li>
              ))}
            </ul>
          ) : (
            <p className="summary-empty">Add bottled juices to keep your guests refreshed.</p>
          )}

          <div className="summary-total">
            <span>Total estimate</span>
            <span>{total}k</span>
          </div>
        </div>

        <div className="customer-form">
          <h3>Booking Contact</h3>
          <div className="form-group">
            <label>Full Name *</label>
            <input 
              type="text" 
              value={customerName}
              onChange={(e) => setCustomerName(e.target.value)}
              placeholder="Enter your name"
              required
            />
          </div>
          <div className="form-group">
            <label>Phone Number *</label>
            <input 
              type="tel" 
              value={customerPhone}
              onChange={(e) => setCustomerPhone(e.target.value)}
              placeholder="0896894688"
              required
            />
          </div>
          <div className="form-group">
            <label>Delivery Address *</label>
            <input 
              type="text" 
              value={customerAddress}
              onChange={(e) => setCustomerAddress(e.target.value)}
              placeholder="Enter your delivery address"
              required
            />
          </div>
          <div className="form-grid">
            <div className="form-group">
              <label>Event Date *</label>
              <input
                type="date"
                value={eventDate}
                onChange={(e) => setEventDate(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label>Preferred Delivery Window</label>
              <input
                type="text"
                value={deliveryWindow}
                onChange={(e) => setDeliveryWindow(e.target.value)}
                placeholder="e.g. 11:30 - 12:00"
              />
            </div>
          </div>
          <div className="form-grid">
            <div className="form-group">
              <label>Guests / Team Size</label>
              <input
                type="number"
                min="1"
                value={peopleCount}
                onChange={(e) => setPeopleCount(e.target.value)}
                placeholder="Number of people"
              />
            </div>
            <div className="form-group">
              <label>Special Requests</label>
              <input
                type="text"
                value={notes}
                onChange={(e) => setNotes(e.target.value)}
                placeholder="Allergies, dietary notes, building access..."
              />
            </div>
          </div>
        </div>

        <div className="action-buttons">
          <button className="btn-submit" onClick={handleSubmit}>
            ‚úÖ Submit Booking Details
          </button>
          <button className="btn-cancel" onClick={onCancel}>
            Cancel Order
          </button>
        </div>

        <p className="info-note">
          ‚ö†Ô∏è Our concierge team will confirm your booking and share payment instructions within 15 minutes.
        </p>
      </div>
    </div>
  );
};

export default PaymentVerification;
