import React from 'react';
import './MenuGrid.css';
import MenuCard from './MenuCard';

const MenuGrid = ({ items, onAddToCart }) => {
  return (
    <div className="menu-container">
      <div className="hero-section">
        <div className="hero-image-container">
          <img src="/images/hero-image.jpg" alt="Fresh Juices" className="hero-image" />
          <div className="hero-overlay">
            <h2>üçπ Fresh Juices, Delivered Fresh!</h2>
            <p>Sip into wellness with our premium juice collection</p>
          </div>
        </div>
      </div>

      <div className="menu-header">
        <h2>Our Menu</h2>
        <p>Choose from our amazing selection</p>
      </div>

      <div className="menu-grid">
        {items.map(item => (
          <MenuCard 
            key={item.id}
            item={item}
            onAddToCart={onAddToCart}
          />
        ))}
      </div>
    </div>
  );
};

export default MenuGrid;
