import React, { useState } from 'react';
import './MenuCard.css';

const MenuCard = ({ item, onAddToCart }) => {
  const [uploadedImage, setUploadedImage] = useState(null);
  const [isUploading, setIsUploading] = useState(false);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setIsUploading(true);
      const reader = new FileReader();
      reader.onloadend = () => {
        setUploadedImage(reader.result);
        setIsUploading(false);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="menu-card">
      <div className="menu-card-image">
        <input
          type="file"
          accept="image/*"
          onChange={handleImageUpload}
          className="image-upload-input"
          id={`upload-${item.id}`}
        />
        {uploadedImage ? (
          <>
            <img src={uploadedImage} alt={item.name} className="uploaded-image" />
            <label htmlFor={`upload-${item.id}`} className="upload-area image-overlay">
              <span className="upload-icon">📷</span>
              <span className="upload-text">Change image</span>
            </label>
          </>
        ) : isUploading ? (
          <div className="upload-area">
            <span className="upload-icon">⏳</span>
            <span className="upload-text">Uploading...</span>
          </div>
        ) : (
          <>
            {item.image.startsWith('/') ? (
              <img src={item.image} alt={item.name} className="default-image" />
            ) : (
              <span className="emoji-large">{item.image}</span>
            )}
            <label htmlFor={`upload-${item.id}`} className="upload-area">
              <span className="upload-icon">📷</span>
              <span className="upload-text">Upload image</span>
            </label>
          </>
        )}
      </div>
      <div className="menu-card-content">
        <h3>{item.name}</h3>
        {item.benefits && (
          <p className="menu-card-benefits">{item.benefits}</p>
        )}
        {!item.benefits && (
          <p className="menu-card-description">{item.description}</p>
        )}
        {item.ingredients && (
          <div className="menu-card-ingredients">
            {item.ingredients.map((ingredient, idx) => (
              <span key={idx} className="ingredient-tag">{ingredient}</span>
            ))}
          </div>
        )}
        <div className="menu-card-footer">
          <span className="price">{item.price}k</span>
          <button className="add-to-cart-btn" onClick={() => onAddToCart(item)}>
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
};

export default MenuCard;
