import React from 'react';
import './MenuGrid.css';
import MenuCard from './MenuCard';
import { getMenuSections, getSectionTitle } from '../utils/menuSections';

const MenuGrid = ({ items, onAddToCart }) => {
  const sections = getMenuSections(items);
  const sectionOrder = [
    "Signature Rice Platters",
    "Family Rice Combos",
    "Sides & Salads",
    "Fresh Juice Extras"
  ];

  return (
    <div className="menu-container">
      <div className="hero-section">
        <div className="hero-image-container">
          <img src="/images/menu-hero.jpg" alt="Corporate Rice Catering" className="hero-image" />
          <div className="hero-overlay">
            <h2>üçö Rice Banquet Booking Hub</h2>
            <p>Curated rice platters with juices as premium extras for your team</p>
          </div>
        </div>
      </div>

      {sectionOrder.map(category => sections[category] && (
        <div key={category} className="menu-section">
          <h2 className="section-title">{getSectionTitle(category)}</h2>
          <div className="menu-grid">
            {sections[category].map(item => (
              <MenuCard 
                key={item.id}
                item={item}
                onAddToCart={onAddToCart}
              />
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default MenuGrid;
